use floem::{
    reactive::{RwSignal, SignalGet},
    views::{svg, Decorators},
    IntoView,
};

use crate::theme::PhazeTheme;

/// Render an SVG icon at `size`Ã—`size` with a theme-derived color.
pub fn phaze_icon(
    svg_str: &'static str,
    size: f32,
    color_fn: impl Fn(&crate::theme::PhazePalette) -> floem::peniko::Color + 'static,
    theme: RwSignal<PhazeTheme>,
) -> impl IntoView {
    svg(move || svg_str.to_string())
    .style(move |s| {
        let t = theme.get();
        let color = color_fn(&t.palette);
        s.width(size).height(size).color(color)
    })
}

